import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { itemService } from '../services/api';

const ItemForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const isEditMode = !!id;

  // Fetch item data if in edit mode
  useEffect(() => {
    if (isEditMode) {
      const fetchItem = async () => {
        try {
          const response = await itemService.getById(id);
          setFormData({
            title: response.data.title,
            description: response.data.description,
          });
        } catch (err) {
          setError('Failed to fetch item data');
        }
      };

      fetchItem();
    }
  }, [id, isEditMode]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const token = localStorage.getItem('token');  // Ensure the token is retrieved from localStorage

      if (!token) {
        setError('No authentication token found');
        setLoading(false);
        return;
      }

      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      };

      if (isEditMode) {
        // Update the item
        await itemService.update(id, formData, { headers });
        navigate(`/items/${id}`);
      } else {
        // Create a new item
        const response = await itemService.create(formData, { headers });
        navigate(`/items/${response.data.id}`);
      }
    } catch (err) {
      setError('Failed to save item');
      setLoading(false);
    }
  };

  return (
    <div className="item-form">
      <h1>{isEditMode ? 'Edit Item' : 'Create New Item'}</h1>
      {error && <div className="error">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
            rows="5"
          />
        </div>

        <div className="form-actions">
          <button type="submit" disabled={loading}>
            {loading ? 'Saving...' : 'Save'}
          </button>
          <button type="button" onClick={() => navigate('/items')}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ItemForm;
